def count_good_pairs(l, r, x, y):
    # Find the number of divisors of y that are between l and r
    count = 0
    for i in range(1, int(y**0.5) + 1):
        if y % i == 0:
            if i % x == 0 and i >= l and i <= r and y // i >= l and y // i <= r:
                if i * i == y:
                    count += 1
                else:
                    count += 2
    return count

# Read input values
l, r, x, y = map(int, input().split())

# Call the function and print the output
