def count_possible_pairs(s, x):
    if (s - x) % 2 != 0 or s < x:
        return 0
    a = (s - x) // 2
    b = s - a
    if (a ^ b) == x:
        return 2 if a != b else 1
    else:
        return 0

# Read input values
s, x = map(int, input().split())

