def longest_common_subsequence(s1, s2, virus):
    def is_subsequence(s, t):
        i, j = 0, 0
        while i < len(s) and j < len(t):
            if s[i] == t[j]:
                j += 1
            i += 1
        return j == len(t)

    def lcs(s1, s2):
        dp = [["" for _ in range(len(s2) + 1)] for _ in range(len(s1) + 1)]
        for i in range(1, len(s1) + 1):
            for j in range(1, len(s2) + 1):
                if s1[i - 1] == s2[j - 1]:
                    dp[i][j] = dp[i - 1][j - 1] + s1[i - 1]
                else:
                    dp[i][j] = max(dp[i - 1][j], dp[i][j - 1], key=len)
        return dp[len(s1)][len(s2)]

    lcs_result = lcs(s1, s2)
    for i in range(len(lcs_result), 0, -1):
        for j in range(len(lcs_result) - i + 1):
            if not is_subsequence(virus, lcs_result[j:j + i]):
                return lcs_result[j:j + i]
    return 0

# Read input strings
s1 = input()
s2 = input()
virus = input()

# Call the function and print the output
print(longest_common_subsequence(s1, s2, virus))

