def count_lucky_numbers(n, k):
    # Function to check if a number is lucky
    def is_lucky(num):
        return all(digit in ['4', '7'] for digit in str(num))

    # Initialize a list to store the lucky numbers in the permutation
    lucky_numbers = []

    # Iterate over the range of numbers from 1 to n
    for i in range(1, n + 1):
        # Check if the index and the number are both lucky
        if is_lucky(i) and is_lucky(k):
            lucky_numbers.append(i)

    # Return the count of lucky numbers in the permutation
    return len(lucky_numbers)

# Read the input values
