n, t = map(int, input().split())

# Initialize a 3D array to store the number of different polylines that can be drawn
dp = [[[0] * 5 for _ in range(5)] for _ in range(n + 1)]

# Set base cases for t = 1
for i in range(1, 5):
    dp[2][i][i] = 1

# Iterate over the number of humps
for i in range(3, n + 1):
    for j in range(1, 5):
        for k in range(j + 1, 5):
            for l in range(1, j):
                dp[i][j][k] += dp[i - 1][l][j]
            for l in range(k + 1, 5):
                dp[i][j][k] += dp[i - 1][k][l]

# Calculate the total number of different polylines for t humps
ans = sum(dp[n][i][j] for i in range(1, 5) for j in range(i + 1, 5))

# Print the result
print(ans)
