function findNthSmallestSuitableNumber($N) {
    $count = 0;
    $x = 1;
    while ($count < $N) {
        for ($a = 1; $a < $x; $a++) {
            for ($b = $a + 1; $b <= $x; $b++) {
                if ($b * $b - $a * $a == $x) {
                    $count++;
                    if ($count == $N) {
                        return $x;
                    }
                }
            }
        }
        $x++;
    }
}

