function findValidPermutations($n, $x, $pos) {
    $MOD = 1000000007;
    $left = $x - 1;
    $right = $n - $x;
    $countLeft = $pos;
    $countRight = $n - pos - 1;

    $result = 1;

    for ($i = 1; $i <= $n; $i++) {
        if ($i < $left) {
            $result = ($result * $countLeft) % $MOD;
            $countLeft--;
        } elseif ($i > $right) {
            $result = ($result * $countRight) % $MOD;
            $countRight--;
        }
    }

    for ($i = 1; $i <= $n - 1; $i++) {
        $result = ($result * $i) % $MOD;
    }

    return $result;
}

