function minMaxRebuilds($n, $m, $roads, $k, $path) {
    // Create an adjacency list to represent the roads
    $adjList = [];
    foreach ($roads as $road) {
        $u = $road[0];
        $v = $road[1];
        $adjList[$u][] = $v;
    }

    // Find the minimum and maximum number of rebuilds
    $minRebuilds = 0;
    $maxRebuilds = 0;
    for ($i = 0; $i < $k - 1; $i++) {
        $current = $path[$i];
        $next = $path[$i + 1];
        if (!in_array($next, $adjList[$current])) {
            $minRebuilds++;
            $maxRebuilds++;
        } else {
            $shortestPath = dijkstra($adjList, $current, $path[$k - 1]);
            $j = $i + 1;
            while ($j < $k - 1 && in_array($path[$j + 1], $adjList[$next])) {
                $j++;
            }
            $i = $j - 1;
        }
    }

    return [$minRebuilds, $maxRebuilds];
}

function dijkstra($adjList, $start, $end) {
    $dist = array_fill(1, count($adjList), PHP_INT_MAX);
    $dist[$start] = 0;
    $pq = new SplPriorityQueue();
    $pq->insert($start, 0);

    while (!$pq->isEmpty()) {
        $u = $pq->extract();
        foreach ($adjList[$u] as $v) {
            $alt = $dist[$u] + 1;
            if ($alt < $dist[$v]) {
                $dist[$v] = $alt;
                $pq->insert($v, -$alt);
            }
        }
    }

    return $dist[$end];
}

