function expectedValue($n, $k) {
    $mod = 1000000007;
    $result = 0;

    for ($i = 1; $i <= $n; $i++) {
        if ($n % $i == 0) {
            $result = ($result + $i) % $mod;
        }
    }

    $exp = 1;
    for ($i = 1; $i <= $k; $i++) {
        $exp = ($exp * $result) % $mod;
    }

    $inverseQ = 1;
    $q = $exp;
    $p = $mod - 2;
    while ($p > 0) {
        if ($p % 2 == 1) {
            $inverseQ = ($inverseQ * $q) % $mod;
        }
        $q = ($q * $q) % $mod;
        $p = (int)($p / 2);
    }

