<?php
function prime($x) {
    $primes = [];
    for ($i = 2; $i * $i <= $x; $i++) {
        while ($x % $i == 0) {
            $primes[] = $i;
            $x /= $i;
        }
    }
    if ($x > 1) {
        $primes[] = $x;
    }
    return array_unique($primes);
}

function g($x, $p) {
    $k = 0;
    while ($x % $p == 0) {
        $k++;
        $x /= $p;
    }
    return pow($p, $k);
}

function f($x, $y) {
    $primes = prime($x);
    $result = 1;
    foreach ($primes as $p) {
        $result *= g($y, $p);
        $result %= (pow(10, 9) + 7);
    }
    return $result;
}

function calculateFormula($x, $n) {
    $result = 1;
    for ($i = 1; $i <= $n; $i++) {
        $result *= f($x, $i);
        $result %= (pow(10, 9) + 7);
    }
    return $result;
}

