function uniqueMaximumMatching($n, $edges) {
    $mod = 998244353;
    $adj = array_fill(0, $n, []);
    foreach ($edges as $edge) {
        $u = $edge[0] - 1;
        $v = $edge[1] - 1;
        $adj[$u][] = $v;
        $adj[$v][] = $u;
    }

    $dp = array_fill(0, $n, 0);
    $dp2 = array_fill(0, $n, 0);

    function add(&$a, $b, $mod) {
        $a += $b;
        if ($a >= $mod) {
            $a -= $mod;
        }
    }

    function dfs($v, $p, $adj, &$dp, &$dp2) {
        $dp[$v] = 1;
        $dp2[$v] = 1;
        foreach ($adj[$v] as $u) {
            if ($u == $p) continue;
            dfs($u, $v, $adj, $dp, $dp2);
            $tmp = $dp[$v];
            $dp[$v] = $dp[$v] * ($dp[$u] + $dp2[$u]) % $mod;
            $dp2[$v] = $dp2[$v] * $dp[$u] % $mod;
            $dp[$u] = $tmp * $dp[$u] % $mod;
        }
    }

    dfs(0, -1, $adj, $dp, $dp2);
    return ($dp[0] + $dp2[0]) % $mod;
}

