        $numerator = ($numerator + $i) % $mod;
    }

    for ($i = 1; $i < $p; $i++) {
        $denominator = ($denominator * $i) % $mod;
    }

    $denominator = ($denominator * $p) % $mod;

    $result = ($numerator * pow($denominator, $mod - 2, $mod)) % $mod;

    return $result;
}

