function faultRate($n, $k) {
    $mod = 1000000007;
    $fact = 1;
    for ($i = 1; $i <= $n; $i++) {
        $fact = ($fact * $i) % $mod;
    }
    $inv = 1;
    for ($i = 1; $i <= $k; $i++) {
        $inv = ($inv * $i) % $mod;
    }
    for ($i = $k + 1; $i <= $n; $i++) {
        $inv = ($inv * $i) % $mod;
    }
    $inv = modInverse($inv, $mod);
    $ans = $fact * $inv;
    return $ans % $mod;
}

function modInverse($a, $m) {
    $m0 = $m;
    $y = 0;
    $x = 1;
    if ($m == 1) {
        return 0;
    }
    while ($a > 1) {
        $q = (int)($a / $m);
        $t = $m;
        $m = $a % $m;
        $a = $t;
        $t = $y;
        $y = $x - $q * $y;
        $x = $t;
    }
    if ($x < 0) {
        $x += $m0;
    }
    return $x;
}

