function findPermutation($n, $k) {
    $factorial = 1;
    $permutation = [];

    for ($i = 1; $i <= $n; $i++) {
        $factorial *= $i;
        $permutation[] = $i;
    }

    $k--;

    for ($i = 0; $i < $n; $i++) {
        $factorial /= ($n - $i);
        $index = $i + $k / $factorial;
        $temp = $permutation[$i];
        for ($j = $i; $j > $index; $j--) {
            $permutation[$j] = $permutation[$j - 1];
        }
        $permutation[$index] = $temp;
        $k %= $factorial;
    }

    return implode(' ', $permutation);
}

