function minimumBitcoinsToWin($HPY, $ATKY, $DEFY, $HPM, $ATKM, $DEFM, $h, $a, $d) {
    $bitcoinsNeeded = 0;

    while ($HPM > 0 && $HPY > 0) {
        $monsterDamage = max(0, $ATKY - $DEFM);
        $yangDamage = max(0, $ATKM - $DEFY);

        $HPM -= $monsterDamage;
        $HPY -= $yangDamage;

        if ($HPM <= 0 && $HPY > 0) {
            break;
        }

        if ($HPY <= 0) {
            $bitcoinsNeeded += $h;
            $HPY = 1;
        }

        if ($ATKY <= $ATKM) {
            $bitcoinsNeeded += $a;
            $ATKY++;
        }

        if ($DEFY <= $DEFM) {
            $bitcoinsNeeded += $d;
            $DEFY++;
        }
    }

    return $bitcoinsNeeded;
}

