<?php
function maxGrade($n, $k, $times, $connections) {
    $adjList = array_fill(0, $n, []);
    for ($i = 0; $i < $n - 1; $i++) {
        list($u, $v) = $connections[$i];
        $adjList[$u - 1][] = $v - 1;
        $adjList[$v - 1][] = $u - 1;
    }

    $visited = array_fill(0, $n, false);
    $minTimes = PHP_INT_MAX;

    function dfs($node, $parent, $times, $visited, $adjList, $k, &$minTimes) {
        $visited[$node] = true;
        $minTimes = min($minTimes, $times[$node]);
        $count = 0;
        foreach ($adjList[$node] as $neighbor) {
            if ($neighbor != $parent) {
                dfs($neighbor, $node, $times, $visited, $adjList, $k, $minTimes);
                $count++;
            }
        }
        if ($count == 0 || $count == 1) {
            $minTimes = min($minTimes, $times[$node]);
        }
    }

    for ($i = 0; $i < $n; $i++) {
        $visited = array_fill(0, $n, false);
        $minTimes = PHP_INT_MAX;
        dfs($i, -1, $times, $visited, $adjList, $k, $minTimes);
        if ($i == 0) {
            $result = $minTimes;
        } else {
            $result = min($result, $minTimes);
        }
    }

    return $result;
}

