            $count *= 10;
        } elseif (ctype_digit($s[$i])) {
            if ($letters[ord($s[$i]) - ord('A')] == -1) {
                $letters[ord($s[$i]) - ord('A')] = intval($s[$i]);
            } elseif ($letters[ord($s[$i]) - ord('A')] != intval($s[$i])) {
                return 0;
            }
        } else {
            if ($used[intval($s[$i])]) {
                $count *= 9;
            } else {
                $used[intval($s[$i])] = true;
            }
        }
    }

    return $count;
}

