function longestCommonSubsequenceWithoutVirus($s1, $s2, $virus) {
    $n = strlen($s1);
    $m = strlen($s2);
    $dp = array_fill(0, $n + 1, array_fill(0, $m + 1, 0));

    for ($i = 1; $i <= $n; $i++) {
        for ($j = 1; $j <= $m; $j++) {
            if ($s1[$i - 1] == $s2[$j - 1]) {
                $dp[$i][$j] = $dp[$i - 1][$j - 1] + 1;
            } else {
                $dp[$i][$j] = max($dp[$i - 1][$j], $dp[$i][$j - 1]);
            }
        }
    }

    $lcs = "";
    $i = $n;
    $j = $m;
    while ($i > 0 && $j > 0) {
        if ($s1[$i - 1] == $s2[$j - 1]) {
            $lcs = $s1[$i - 1] . $lcs;
            $i--;
            $j--;
        } elseif ($dp[$i - 1][$j] > $dp[$i][$j - 1]) {
            $i--;
        } else {
            $j--;
        }
    }

    if (strpos($lcs, $virus) !== false) {
        return "0";
    } else {
        return $lcs;
