function findInitialPermutation($n, $m, $operations) {
    $cups = range(1, $n);

    foreach ($operations as $operation) {
        $x = $operation[0];
        $y = $operation[1];

        if ($cups[$y-1] == $x) {
            continue;
        } else {
            $index = array_search($x, $cups);
            if ($index === false) {
                return -1;
            }
            array_splice($cups, $index, 1);
            array_unshift($cups, $x);
        }
    }

    return implode(' ', $cups);
}

