function countLuckyNumbers($n, $k) {
    // Function to check if a number is lucky
    function isLucky($num) {
        while ($num > 0) {
            $digit = $num % 10;
            if ($digit != 4 && $digit != 7) {
                return false;
            }
            $num = (int)($num / 10);
        }
        return true;
    }

    // Check if the k-th permutation exists
    $factorial = 1;
    for ($i = 1; $i <= $n; $i++) {
        $factorial *= $i;
        if ($factorial < $k) {
            return -1;
        }
    }

    // Generate the k-th permutation
    $permutation = range(1, $n);
    for ($i = 0; $i < $k - 1; $i++) {
        next_permutation($permutation);
    }

    // Count the number of lucky numbers at lucky positions
    $count = 0;
    for ($i = 0; $i < $n; $i++) {
        if (isLucky($i + 1) && isLucky($permutation[$i])) {
            $count++;
        }
    }

    return $count;
}

// Function to generate next permutation
function next_permutation(&$array) {
    $size = count($array);
    for ($i = $size - 2; $i >= 0; $i--) {
        if ($array[$i] < $array[$i + 1]) {
            for ($j = $size - 1; $j > $i; $j--) {
                if ($array[$j] > $array[$i]) {
                    list($array[$i], $array[$j]) = array($array[$j], $array[$i]);
                    array_splice($array, $i + 1, $size - $i - 1, array_reverse(array_slice($array, $i + 1)));
                    return;
                }
            }
        }
    }
    reverse($array, 0, $size - 1);
}

// Function to reverse a portion of the array
function reverse(&$array, $start, $end) {
    while ($start < $end) {
        list($array[$start], $array[$end]) = array($array[$end], $array[$start]);
        $start++;
        $end--;
    }
}

