function findInterestingStates($n, $m, $actions, $transitions) {
    $interesting = array_fill(0, $n, 0);

    for ($i = 0; $i < $n; $i++) {
        if ($actions[$i] == 2) {
            $visited = array_fill(0, $n, false);
            $stack = [$i];
            $visited[$i] = true;
            $foundAssign = false;

            while (!empty($stack)) {
                $current = array_pop($stack);

                if ($actions[$current] == 1) {
                    $foundAssign = true;
                }

                foreach ($transitions as $transition) {
                    if ($transition[0] == $current && !$visited[$transition[1]]) {
                        $stack[] = $transition[1];
                        $visited[$transition[1]] = true;
                    }
                }
            }

            if ($foundAssign) {
                $interesting[$i] = 1;
            }
        }
    }

    return implode("\n", $interesting);
}

