function countCamelsUtil($n, $t, $index, $prevY, $humps) {
    if ($index == $n) {
        if ($humps == $t) {
            return 1;
        } else {
            return 0;
        }
    }
    $count = 0;
    for ($i = 1; $i <= 4; $i++) {
        if (($prevY < $i && $humps < $t) || ($prevY > $i && $humps == $t)) {
            $count += countCamelsUtil($n, $t, $index + 1, $i, $humps + ($prevY < $i ? 1 : 0));
        }
    }
    return $count;
}

