<?php
function minimumTime($l, $d, $v, $g, $r) {
    $time = $d / $v; // time to reach traffic lights
    $remainder = $time / ($g + $r); // number of complete cycles
    $remainderTime = $time % ($g + $r); // remaining time after complete cycles

    if ($remainderTime <= $g) {
        $time += $g - $remainderTime; // wait for green light
    } else {
        $time += $g + $r - $remainderTime; // wait for next green light
    }

    $time += ($l - $d) / $v; // time to reach point B
    return $time;
}

