function transformEmailAddress($input) {
  $email = str_replace("dot", ".", $input);
  $email = str_replace("at", "@", $email);

  // Check if the email starts or ends with a dot or at sign
  while ($email[0] == '.' || $email[0] == '@') {
    $email = substr($email, 1);
  }
  while ($email[strlen($email) - 1] == '.' || $email[strlen($email) - 1] == '@') {
    $email = substr($email, 0, -1);
  }

  // Split the email address at the @ symbol
  $parts = explode('@', $email);

  // Replace multiple dots with a single dot in the domain part
  $parts[1] = preg_replace("/\.{2,}/", ".", $parts[1]);

  // Reconstruct the email address
  $result = $parts[0] . '@' . $parts[1];

  return $result;
}

