def count_ways(n, k):
    # Function to calculate the number of ways to express n as a product of k positive integers
    if k == 1:
        return 1  # Only one way to express n as a product of one integer (itself)

    # Dynamic programming table to store the number of ways
    dp = [[0] * (n + 1) for _ in range(k + 1)]
    dp[0][1] = 1  # Base case: 1 way to multiply to 1 with 0 integers

    for i in range(1, k + 1):
        for j in range(1, n + 1):
            for x in range(1, j + 1):
                if j % x == 0:  # x must be a divisor of j
                    dp[i][j] += dp[i - 1][j // x]

    return dp[k][n]

