def transform_sentence(sentence):
    import re

    def transform_word(word):
        # Check if the word is longer than 6 characters or has 2 or more 'T'/'t'
        if len(word) > 6 or word.lower().count('t') >= 2:
            return word[::-1]  # Reverse the word
        elif len(word) == 2:
            return word.upper()  # Uppercase the word
        elif len(word) == 1:
            return '0'  # Convert to "0"
        else:
            return word  # No transformation

    # Split the sentence into words while keeping punctuation
    words = re.findall(r'\b\w+\b|[^\w\s]', sentence)

    transformed_words = []

    for word in words:
        if word.isalpha():  # Only transform words
            transformed_words.append(transform_word(word))
        else:
            transformed_words.append(word)  # Keep punctuation as is

    # Join the transformed words back into a sentence
    return ' '.join(transformed_words)

