def can_escape(n, q, queries):
    # Initialize the grid state
    grid = [[0] * n for _ in range(2)]  # 0 for ground, 1 for lava

    # Function to check if escape is possible
    def is_possible():
        # Check if both (1, 1) and (2, n) are reachable
        if grid[0][0] == 1 or grid[1][n - 1] == 1:
            return False

        # Check the first row
        reachable = [False] * n
        reachable[0] = True

        for j in range(n):
            if reachable[j]:
                if grid[0][j] == 0:  # Can move to (1, j)
                    if j + 1 < n and grid[0][j + 1] == 0:
                        reachable[j + 1] = True
                    if j + 1 < n and grid[1][j + 1] == 0:
                        reachable[j + 1] = True
                if grid[1][j] == 0:  # Can move to (2, j)
                    if j + 1 < n and grid[1][j + 1] == 0:
                        reachable[j + 1] = True
                    if j + 1 < n and grid[0][j + 1] == 0:
                        reachable[j + 1] = True

        return reachable[n - 1]

    results = []

    for r, c in queries:
        # Toggle the cell state
        grid[r - 1][c - 1] ^= 1  # Flip between 0 and 1

        # Check if escape is possible after the toggle
        if is_possible():
            results.append("Yes")
        else:
            results.append("No")

    return results

