def minimum_language_cost(n, m, employees):
    # Create a list to track which languages are known by employees
    language_known = [set() for _ in range(m + 1)]

    # Fill the language_known list
    for i in range(n):
        for lang in employees[i]:
            language_known[lang].add(i)

    # Create a list to track which employees are connected through languages
    connected = [False] * n

    # Find connected components using a DFS approach
    def dfs(employee):
        stack = [employee]
        while stack:
            emp = stack.pop()
            if not connected[emp]:
                connected[emp] = True
                for lang in employees[emp]:
                    for other_emp in language_known[lang]:
                        if not connected[other_emp]:
                            stack.append(other_emp)

    # Count the number of connected components
    components = 0
    for i in range(n):
        if not connected[i]:
            dfs(i)
            components += 1

    # If all employees know at least one language, no cost is needed
    if components == 1:
        return 0

    # If there are employees who know no languages, we need to teach them at least one language
    # The minimum cost is the number of disconnected components minus one
    return components - 1

