def mix(s1, s2):
    from collections import Counter

    # Count frequencies of lowercase letters in both strings
    count1 = Counter(c for c in s1 if 'a' <= c <= 'z')
    count2 = Counter(c for c in s2 if 'a' <= c <= 'z')

    # Dictionary to hold the maximum counts
    max_counts = {}

    # Compare counts from both strings
    for char in set(count1.keys()).union(set(count2.keys())):
        if count1[char] > count2[char]:
            max_counts[char] = (1, count1[char])  # (string number, count)
        elif count2[char] > count1[char]:
            max_counts[char] = (2, count2[char])  # (string number, count)
        elif count1[char] == count2[char] and count1[char] > 1:
            max_counts[char] = (0, count1[char])  # (equal, count)

    # Prepare the result list
    result = []
    for char, (source, count) in max_counts.items():
        if count > 1:
            if source == 0:
                result.append(f"=:{char * count}")
            else:
                result.append(f"{source}:{char * count}")

    # Sort the result by length (descending) and then lexicographically
    result.sort(key=lambda x: (-len(x), x))

    # Join the results with '/'
    return '/'.join(result)

