def balance_boxes():
    import sys
    input = sys.stdin.read
    data = input().splitlines()

    k = int(data[0])
    boxes = []
    total_sum = 0

    for i in range(1, k + 1):
        line = list(map(int, data[i].split()))
        n_i = line[0]
        numbers = line[1:]
        boxes.append(numbers)
        total_sum += sum(numbers)

    # Check if it's possible to balance
    if total_sum % k != 0:
        print("No")
        return

    target_sum = total_sum // k
    current_sums = [sum(box) for box in boxes]

    # We need to find a way to balance the boxes
    from collections import defaultdict

    # Store the numbers in each box
    number_to_box = defaultdict(list)
    for i in range(k):
        for number in boxes[i]:
            number_to_box[number].append(i)

    # Prepare to find the moves
    moves = []
    for i in range(k):
        while current_sums[i] < target_sum:
            # Need to increase the sum of box i
            deficit = target_sum - current_sums[i]
            # Find a number to take from another box
            for j in range(k):
                if i != j and current_sums[j] > target_sum:
                    # We can take from box j
                    for number in boxes[j]:
                        if number <= deficit:
                            # Make the move
                            moves.append((number, i + 1))  # number from box j to box i
                            current_sums[i] += number
                            current_sums[j] -= number
                            break
                    if current_sums[i] >= target_sum:
                        break

        while current_sums[i] > target_sum:
            # Need to decrease the sum of box i
            surplus = current_sums[i] - target_sum
            # Find a number to take from box i to another box
            for j in range(k):
                if i != j and current_sums[j] < target_sum:
                    # We can give to box j
                    for number in boxes[i]:
                        if number <= surplus:
