def total_inc_dec(x):
    if x == 0:
        return 1
    elif x == 1:
        return 10

    # Dynamic programming table
    dp = [[0] * 10 for _ in range(x + 1)]

    # Base case: for 1-digit numbers (0-9)
    for i in range(10):
        dp[1][i] = 1

    # Fill the dp table
    for length in range(2, x + 1):
        for last_digit in range(10):
            for prev_digit in range(last_digit + 1):
                dp[length][last_digit] += dp[length - 1][prev_digit]

    # Calculate total increasing numbers
    total_increasing = sum(dp[length][last_digit] for length in range(1, x + 1) for last_digit in range(10))

    # Reset dp for decreasing numbers
    dp = [[0] * 10 for _ in range(x + 1)]

    # Base case: for 1-digit numbers (0-9)
    for i in range(10):
        dp[1][i] = 1

    # Fill the dp table for decreasing numbers
    for length in range(2, x + 1):
        for last_digit in range(10):
            for prev_digit in range(last_digit, 10):
                dp[length][last_digit] += dp[length - 1][prev_digit]

    # Calculate total decreasing numbers
    total_decreasing = sum(dp[length][last_digit] for length in range(1, x + 1) for last_digit in range(10))

    # Total increasing and decreasing numbers
    total = total_increasing + total_decreasing - 10  # Subtract 10 for double counting 0-9

    return total

