def countWays(n, k, min_factor=1):
    if k == 1:
        return 1 if n >= min_factor else 0
    if n < min_factor:
        return 0

    total_ways = 0
    for i in range(min_factor, n + 1):
        if n % i == 0:
            total_ways += countWays(n // i, k - 1, i)

    return total_ways

def main():
    n = int(input().strip())
    k = int(input().strip())
    result = countWays(n, k)
    print(result)

if __name__ == "__main__":
    main()

