def lucky_permutation(n):
    if n % 2 == 0:
        print(-1)
    else:
        p = [0] * n
        for i in range(1, n + 1):
            if i % 2 == 1:  # odd index
                p[i - 1] = i + 1 if i + 1 <= n else i - 1
            else:  # even index
                p[i - 1] = i - 1
        print(' '.join(map(str, p)))

