def solve_expression(expression):
    left, right = expression.split('=')
    left_operand, operator, right_operand = left[:-1], left[-1], right

    existing_digits = set()
    for char in expression:
        if char.isdigit():
            existing_digits.add(char)

    possible_digits = [str(d) for d in range(10) if str(d) not in existing_digits]
    valid_digits = []

    for digit in possible_digits:
        new_left = left_operand.replace('?', digit)
        new_right = right_operand.replace('?', digit)

        if (new_left.startswith('0') and new_left != '0') or (new_right.startswith('0') and new_right != '0'):
            continue

        if operator == '+':
            if int(new_left) + int(new_right) == int(right):
                valid_digits.append(int(digit))
        elif operator == '-':
            if int(new_left) - int(new_right) == int(right):
                valid_digits.append(int(digit))
        elif operator == '*':
            if int(new_left) * int(new_right) == int(right):
                valid_digits.append(int(digit))

    return min(valid_digits) if valid_digits else -1

# Input and output handling
if __name__ == "__main__":
    expression = input().strip()
    result = solve_expression(expression)
    print(result)

