def cover_graph_with_cycles():
    import sys
    input = sys.stdin.read
    n = int(input().strip())

    if n < 3:
        print(-1)
        return

    cycles = []

    if n % 2 == 0:
        # For even n, create n/2 cycles of length 4
        for i in range(1, n + 1, 4):
            cycles.append([4, i, i + 1, i + 2, i + 3])
    else:
        # For odd n, create (n-3)/2 cycles of length 4 and one cycle of length 3
        for i in range(1, n - 2, 4):
            cycles.append([4, i, i + 1, i + 2, i + 3])
        # Last three vertices form a cycle of length 3
        cycles.append([3, n - 2, n - 1, n])

    # Output the result
    print(len(cycles))
    for cycle in cycles:
        print(" ".join(map(str, cycle)))

cover_graph_with_cycles()

