def exp_sum(n):
    dp = [0] * (n + 1)
    dp[0] = 1  # Base case: one way to partition 0

    for i in range(1, n + 1):
        for j in range(i, n + 1):
            dp[j] += dp[j - i]

    return dp[n]

if __name__ == "__main__":
    n = int(input().strip())
    print(exp_sum(n))

