class UnionFind:
    def __init__(self, size):
        self.parent = list(range(size))
        self.rank = [1] * size

    def find(self, p):
        if self.parent[p] != p:
            self.parent[p] = self.find(self.parent[p])  # Path compression
        return self.parent[p]

    def union(self, p, q):
        rootP = self.find(p)
        rootQ = self.find(q)
        if rootP != rootQ:
            # Union by rank
            if self.rank[rootP] > self.rank[rootQ]:
                self.parent[rootQ] = rootP
            elif self.rank[rootP] < self.rank[rootQ]:
                self.parent[rootP] = rootQ
            else:
                self.parent[rootQ] = rootP
                self.rank[rootP] += 1

def min_cost_to_connect_employees(n, m, languages):
    uf = UnionFind(m + 1)  # +1 because languages are 1-indexed

    for emp_languages in languages:
        if emp_languages:  # Only process if the employee knows at least one language
            first_language = emp_languages[0]
            for lang in emp_languages[1:]:
                uf.union(first_language, lang)

    # Count unique roots (connected components)
    unique_roots = set()
    for lang in range(1, m + 1):
        if uf.find(lang) != lang:
            unique_roots.add(uf.find(lang))

    # Count employees who know no languages
    no_language_count = sum(1 for emp in languages if not emp)

    # Minimum cost is the number of unique components minus 1, plus one if there are employees with no languages
    return max(len(unique_roots) - 1, 0) + (1 if no_language_count > 0 else 0)

# Input reading
n, m = map(int, input().split())
languages = [list(map(int, input().split()))[1:] for _ in range(n)]

# Calculate and print the result
print(min_cost_to_connect_employees(n, m, languages))

