def mix(s1, s2):
    from collections import Counter

    # Count frequencies of lowercase letters in both strings
    count1 = Counter(c for c in s1 if 'a' <= c <= 'z')
    count2 = Counter(c for c in s2 if 'a' <= c <= 'z')

    result = []

    # Combine counts and determine the maximum occurrences
    for char in set(count1.keys()).union(set(count2.keys())):
        max_count = max(count1[char], count2[char])
        if max_count > 1:
            if count1[char] > count2[char]:
                result.append(f"1:{char * max_count}")
            elif count1[char] < count2[char]:
                result.append(f"2:{char * max_count}")
            else:
                result.append(f"=:{char * max_count}")

    # Sort the results first by length (descending) then lexicographically
    result.sort(key=lambda x: (-len(x), x))

    return '/'.join(result)

# Read input and call the function
s1 = input().strip()
s2 = input().strip()
print(mix(s1, s2))

