def find(s):
    longest = ""
    i = 0

    while i < len(s):
        if s[i] == '!':
            start = i
            while i < len(s) and s[i] == '!':
                i += 1
            if i < len(s) and s[i] == '?':
                current = s[start:i]
                while i < len(s) and s[i] == '?':
                    current += s[i]
                    i += 1
                if len(current) > len(longest):
                    longest = current
        elif s[i] == '?':
            start = i
            while i < len(s) and s[i] == '?':
                i += 1
            if start > 0 and s[start - 1] == '!':
                current = s[start:i]
                while i < len(s) and s[i] == '!':
                    current = s[start - 1] + current + s[i]
                    i += 1
                if len(current) > len(longest):
                    longest = current
            else:
                i += 1
        else:
            i += 1

    return longest

