def alphabet_war(fight):
    left_powers = {'w': 4, 'p': 3, 'b': 2, 's': 1}
    right_powers = {'m': 4, 'q': 3, 'd': 2, 'z': 1}

    def process_bombs(fight):
        fight_list = list(fight)
        n = len(fight)
        for i in range(n):
            if fight[i] == '*':
                if i > 0:
                    fight_list[i - 1] = '_'
                if i < n - 1:
                    fight_list[i + 1] = '_'
        return ''.join(fight_list)

    def calculate_power(fight):
        left_score = 0
        right_score = 0
        for char in fight:
            if char in left_powers:
                left_score += left_powers[char]
            elif char in right_powers:
                right_score += right_powers[char]
        return left_score, right_score

    modified_fight = process_bombs(fight)
    left_score, right_score = calculate_power(modified_fight)

    if left_score > right_score:
        return "Left side wins!"
    elif right_score > left_score:
        return "Right side wins!"
    else:
        return "Let's fight again!"

fight = input().strip()
result = alphabet_war(fight)
print(result)

