def dfs(vertex, parent, tree):
    total_probability = 1.0  # Probability of this vertex surviving
    child_count = 0

    for neighbor in tree[vertex]:
        if neighbor != parent:
            child_count += 1
            child_probability = dfs(neighbor, vertex, tree)
            total_probability *= (1 - child_probability)

    if child_count > 0:
        return total_probability / (2 ** child_count)
    else:
        return 1.0

