def hofstadter_q(n, memo={}):
    if n in memo:
        return memo[n]
    if n == 1 or n == 2:
        return 1
    else:
        # Calculate the two previous values based on the recursive definition
        prev1 = hofstadter_q(n - hofstadter_q(n - 1), memo)
        prev2 = hofstadter_q(n - hofstadter_q(n - 2), memo)
        memo[n] = prev1 + prev2
        return memo[n]

