def simplify(polynomial):
    import re
    from collections import defaultdict

    # Parse the input string into monomials
    monomials = re.findall(r'[\+\-]?\d*[a-z]+', polynomial)

    # Dictionary to aggregate coefficients of normalized monomials
    coeffs = defaultdict(int)

    for monomial in monomials:
        # Determine the sign and coefficient
        if monomial[0] in '+-':
            sign = 1 if monomial[0] == '+' else -1
            monomial = monomial[1:]  # Remove the sign for processing
        else:
            sign = 1

        # Split into coefficient and variables
        if monomial.isalpha():
            coeff = 1
            variables = monomial
        else:
            match = re.match(r'(\d+)([a-z]+)', monomial)
            if match:
                coeff = int(match.group(1))
                variables = match.group(2)
            else:
                continue

        # Normalize variables by sorting them
        normalized_variables = ''.join(sorted(variables))

        # Update the coefficient in the dictionary
        coeffs[normalized_variables] += sign * coeff

    # Filter out zero coefficients and prepare for sorting
    filtered_monomials = {var: coeff for var, coeff in coeffs.items() if coeff != 0}

    # Sort the monomials first by length, then lexicographically
    sorted_monomials = sorted(filtered_monomials.items(), key=lambda x: (len(x[0]), x[0]))

    # Construct the output string
    result = []
    for var, coeff in sorted_monomials:
        if coeff > 0:
            if result:
                result.append(f"+{coeff if coeff != 1 else ''}{var}")
            else:
                result.append(f"{coeff if coeff != 1 else ''}{var}")
        else:
            result.append(f"{coeff}{var}" if coeff != -1 else f"-{var}")

    return ''.join(result)

# Input and output handling
if __name__ == "__main__":
    input_str = input().strip()
    output_str = simplify(input_str)
    print(output_str)

