from itertools import permutations

def solve(s):
    vowels = [c for c in s if c in 'aeiou']
    consonants = [c for c in s if c not in 'aeiou']

    if abs(len(vowels) - len(consonants)) > 1:
        return "failed"

    if len(vowels) == len(consonants):
        start_with_vowel = True
    else:
        start_with_vowel = len(vowels) > len(consonants)

    valid_arrangements = set()

    for v_perm in permutations(vowels):
        for c_perm in permutations(consonants):
            arrangement = []
            v_index, c_index = 0, 0

            for i in range(len(s)):
                if (start_with_vowel and i % 2 == 0) or (not start_with_vowel and i % 2 == 1):
                    if v_index < len(v_perm):
                        arrangement.append(v_perm[v_index])
                        v_index += 1
                else:
                    if c_index < len(c_perm):
                        arrangement.append(c_perm[c_index])
                        c_index += 1

            if v_index == len(v_perm) and c_index == len(c_perm):
                valid_arrangements.add(''.join(arrangement))

    if valid_arrangements:
        return min(valid_arrangements)
    else:
        return "failed"

