import sys
import heapq

def dijkstra(n, graph):
    dist = [float('inf')] * (n + 1)
    dist[1] = 0
    priority_queue = [(0, 1)]  # (distance, node)

    while priority_queue:
        current_dist, u = heapq.heappop(priority_queue)

        if current_dist > dist[u]:
            continue

        for v, length in graph[u]:
            if dist[u] + length < dist[v]:
                dist[v] = dist[u] + length
                heapq.heappush(priority_queue, (dist[v], v))

    return dist

def max_train_routes_can_be_closed(n, m, k, roads, train_routes):
    graph = [[] for _ in range(n + 1)]

    for u, v, x in roads:
        graph[u].append((v, x))
        graph[v].append((u, x))

    dist = dijkstra(n, graph)

    essential_count = 0

    for s, y in train_routes:
        if dist[s] == y:
            essential_count += 1

    return k - essential_count

