def can_form_strictly_increasing_sequence(sequence):
    violation_count = 0
    n = len(sequence)

    for i in range(n - 1):
        if sequence[i] >= sequence[i + 1]:
            violation_count += 1
            if violation_count > 1:
                return False

            # Check if we can skip the current element
            if i > 0 and sequence[i - 1] >= sequence[i + 1]:
                # If we can't skip the current element, we must skip the next one
                if i + 2 < n and sequence[i] >= sequence[i + 2]:
                    return False

    return True

