    exceptions = {
        'c': -1,
        'o': -1,
        'd': -3,
        'e': -4
    }

    result = []

    for char in s:
        if char in vowels:
            new_char = chr((ord(char) - ord('a') - 5) % 26 + ord('a'))
        else:
