def sum_of_dominant_primes(a, b):
    limit = 500000
    is_prime = [True] * (limit + 1)
    primes = []

    # Sieve of Eratosthenes to find all primes up to limit
    is_prime[0] = is_prime[1] = False
    for i in range(2, int(limit**0.5) + 1):
        if is_prime[i]:
            for j in range(i * i, limit + 1, i):
                is_prime[j] = False

    # Collect all primes
    for i in range(limit + 1):
        if is_prime[i]:
            primes.append(i)

    # Identify dominant primes
    dominant_primes = []
    for index in range(1, len(primes) + 1):
        if is_prime[index]:  # Check if the index is prime
            dominant_primes.append(primes[index - 1])  # Add the prime at this index

    # Calculate the sum of dominant primes within the range [a, b]
    return sum(p for p in dominant_primes if a <= p <= b)

# Input handling
