def proper_fractions(d):
    if d <= 1:
        return 0

    result = d
    p = 2

    # Check for every number from 2 to sqrt(d)
    while p * p <= d:
        if d % p == 0:
            # p is a prime factor of d
            while d % p == 0:
                d //= p
            result -= result // p
        p += 1

    # If d is still greater than 1, then it is prime
    if d > 1:
        result -= result // d

    return result

