def almost_everywhere_zero(n, k):
    n_length = len(n)
    count = 0

    # Function to count numbers with exactly k non-zero digits
    def count_valid_numbers(length, k):
        if k == 0 or k > length:
            return 0

        total_count = 0

        # Generate combinations of positions for non-zero digits
        from itertools import combinations

        # Iterate over all combinations of positions for non-zero digits
        for positions in combinations(range(length), k):
            # For each combination of positions, we need to place non-zero digits
            for first_digit in range(1, 10):  # First digit can be 1-9
                # Count the number of ways to fill the remaining positions
                remaining_positions = length - k  # Remaining positions after placing k non-zero digits
                if remaining_positions >= 0:
                    total_count += 9 * (10 ** remaining_positions)  # 9 choices for the first digit, 10 for the rest

        return total_count

    # Count valid numbers with exactly k non-zero digits
    for length in range(k, n_length + 1):
        count += count_valid_numbers(length, k)

    # Now we need to check numbers with exactly k non-zero digits up to n
    def count_non_zero_digits(num):
        return sum(1 for digit in num if digit != '0')

    # Count valid numbers from 1 to n
    for i in range(1, int(n) + 1):
        if count_non_zero_digits(str(i)) == k:
            count += 1

    return count

if __name__ == "__main__":
